import { Component, OnInit } from '@angular/core';
import { Book } from '../model/book.model';
import { BookRepositoryService } from '../services/book-repository.service';
import { BorrowBookService } from '../services/borrow-book.service';

@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.css']
})
export class BookListComponent implements OnInit{
  buttonDisabled: boolean = false;
  ngOnInit(): void {
    this.getBooksBd();
  }
  constructor(
    private bookServiceList: BookRepositoryService,
    private bookBorrowe: BorrowBookService){}

  book:Book[];
bookCreated(book:Book){
    this.book.push(book);
}
private getBooksBd(){
  this.bookServiceList.getBookList().subscribe(books =>{
    this.book = books;
    console.log(books);

  });
}
request(libro:Book){
  alert(`Solicitaste el libro : ${libro.name}`);
  const bookId = libro.id;
    this.bookBorrowe.borrowBook(bookId).subscribe(
      () => {
        alert('Solicitaste el libro');
        this.buttonDisabled = false;
      },
      (error) => {
        alert('Ocurrió un error al solicitar el libro');
        this.buttonDisabled = false;
      }
    );
}
returnBook(libro:Book){
  const bookId = libro.id;
  this.bookBorrowe.returnBook(bookId).subscribe(
    () => {
      // Manejar la respuesta exitosa, por ejemplo, mostrar una alerta
      alert(`Devolviste el Libro -> ${libro.name}`);
      // Habilitar el botón nuevamente si es necesario
      this.buttonDisabled = false;
    },
    (error) => {
      // Manejar errores, por ejemplo, mostrar una alerta de error
      alert(`Ocurrió un error al devolver el libro `);
      // Habilitar el botón nuevamente si es necesario
      this.buttonDisabled = false;
    }
  );
}
}
<div class="container">
  <div class="row">
    <div class="col-md-4 col-lg-3" *ngFor="let libro of book">
      <div class="card mb-4" style="width: 280px;">
        <img src="assets/pngwing.png" class="card-img-top" alt="...">
        <div class="card-body">
          <h5 class="card-title">{{libro.name}}</h5>
          <p class="card-text">ISBN: {{libro.isbn}}</p>
          <button type="submit" class="btn btn-primary request " (click)="request(libro)" [disabled]="buttonDisabled">Solicitar</button>
          <button type="submit" class="btn btn-warning order " (click)="returnBook(libro)">Devolver Libro</button>
        </div>
      </div>
    </div>
  </div>
</div>
