import { Component, EventEmitter, Output } from '@angular/core';
import { Book } from '../model/book.model';

@Component({
  selector: 'app-save-book',
  templateUrl: './save-book.component.html',
  styleUrls: ['./save-book.component.css']
})
export class SaveBookComponent {
  @Output() createBook = new EventEmitter<Book>()
  nameBook:string;
  isbnBook:string;
  borrowed:boolean;
  saveBook(){
    let localBook = new Book(this.nameBook,this.isbnBook,this.borrowed);
    this.createBook.emit(localBook);
  }
}
