import { Component,OnInit  } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  items: any[] = [
    { image: 'https://yt3.googleusercontent.com/ytc/AOPolaSgHdj29kGTCHcPbs-L7vGSYrF8CCyHyKDTbsyKuQ=s900-c-k-c0x00ffffff-no-rj', alt: 'Slide 1' },
    { image: 'assets/assettwo.jpg', alt: 'Slide 2' },
    { image: 'assets/assetthree.jpg', alt: 'Slide 3' },
  ];
    currentIndex: number = 0;
    constructor() { }

  ngOnInit(): void {
    this.startCarousel();
  }
  startCarousel() {
    setInterval(() => {
      this.currentIndex = (this.currentIndex + 1) % this.items.length;
    }, 3000); // Cambia de diapositiva cada 3 segundos (ajusta este valor según tus preferencias)
  }
}
