import { Component, OnInit } from '@angular/core';
import { Book } from '../model/book.model';
import { BookRepositoryService } from '../services/book-repository.service';

@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.css']
})
export class BookListComponent implements OnInit{
  ngOnInit(): void {
    this.getBooksBd();
  }
  constructor(private bookServiceList: BookRepositoryService){}
  book:Book[];
bookCreated(book:Book){
    this.book.push(book);
}
private getBooksBd(){
  this.bookServiceList.getBookList().subscribe(books =>{
    this.book = books;
    console.log(books);

  });
}
}
<div class="container">
  <div class="row">
    <div class="col-md-4 col-lg-3" *ngFor="let libro of book">
      <div class="card mb-4" style="width: 280px;">
        <img src="assets/pngwing.png" class="card-img-top" alt="...">
        <div class="card-body">
          <h5 class="card-title">{{libro.name}}</h5>
          <p class="card-text">ISBN: {{libro.isbn}}</p>
          <button type="submit" class="btn btn-primary request " >Solicitar</button>
          <button type="submit" class="btn btn-primary order " >Solicitar</button>
        </div>
      </div>
    </div>
  </div>
</div>
